#define ll long long int
class Solution {
public:

    //initializing
    int n;
    vector<int>a;
    map<int,int>m;//indices of elements that have been removed!
    priority_queue<array<ll,3>>pq;//store sum as well as thier bounded range and sorting on the basis of maximum sum
    vector<ll>pre;//stores the prefix sum
    vector<ll>ans;

    //insertion into priority queue
    void insert(int l,int r){
        if(l<0||l>=n||r<0||r>=n||l>r)return;
        ll sum=pre[r+1]-pre[l];
        auto x={sum,(ll)l,(ll)r};
        pq.push({sum,l,r});
    }

    //finding the highest segment sum
    ll fun(){
        if(pq.empty())return 0;
        auto x=pq.top();
        auto itr=m.lower_bound(x[1]);
        if(itr==m.end())
            return x[0];
        else{
            if(x[2]<itr->first)
                return x[0];
            else{
                pq.pop();
                return fun();
            }
        }
    }

    //main function
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& q) {
        a=nums;
        n=a.size();

        //prefix sum
        pre.resize(n+1);
        pre[0]=0;
        for(int i=1;i<=n;i++){
            pre[i]=a[i-1]+pre[i-1];
        }

        //handling queries
        for(auto i:q){
            m[i]=1;

            //right segment
            auto r=m.upper_bound(i);
            if(r==m.end())insert(i+1,n-1);
            else insert(i+1,r->first-1);

            //left segment
            auto l=m.lower_bound(i);
            if(l==m.begin())insert(0,i-1);
            else insert(prev(l)->first+1,i-1);

            //maximum among segments
            ans.push_back(fun());
        }

        return ans;
    }
};
