class Solution:
    def maximumSegmentSum(self, nums: List[int], removeQueries: List[int]) -> List[int]:
        def findParent(parent,val):
            if parent[val]==val:
                return val
            parent[val]=findParent(parent,parent[val])
            return parent[val]
        def union(a,b,rank,parent):
            parentA=findParent(parent,a)
            parentB=findParent(parent,b)
            if rank[parentA] > rank[parentB]:
                parent[parentB]=parentA
            elif rank[parentB] > rank[parentA]:
                parent[parentA]=parentB
            else:
                rank[parentA]+=1
                parent[parentB]=parentA
        res=[0]
        maxx=0
        rank={i:0 for i in range(-1,len(nums)+1)}
        parent={i:i for i in range(-1,len(nums)+1)}
        val=rank.copy()
        while removeQueries:
            i=removeQueries.pop()
            v=nums[i]
            val[i]=v
            if val[i-1]!=0:
                parentA=findParent(parent,i-1)
                parentB=findParent(parent,i)
                if rank[parentA] >= rank[parentB]:
                    val[parentA]+=val[parentB]
                else:
                    val[parentB]+=val[parentA]
                maxx=max(maxx,val[parentA],val[parentB])
                union(parentA,parentB,rank,parent)
            if val[i+1]!=0:
                parentA=findParent(parent,i+1)
                parentB=findParent(parent,i)
                if rank[parentA] >= rank[parentB]:
                    val[parentA]+=val[parentB]
                else:
                    val[parentB]+=val[parentA]
                maxx=max(maxx,val[parentA],val[parentB])
                union(parentA,parentB,rank,parent)
            maxx=max(maxx,v)
            res.append(maxx)
        res.pop()
        return res[::-1]
