#define max(x,y) x > y ? x : y
#define ll long long int

class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& que) {
        const int n = nums.size();
        vector<ll> ans(n);
        map<int,ll> m;
        ll maxSum = 0;
        vector<bool> present(n,false);
        for(int i = n - 1;i >= 0;i--){
            ll sum = 0,q = que[i],cur = q;
            present[q] = true;
            if(q > 0 && present[q - 1]){
                sum += m[q - 1];
                m.erase(q-1);
            }
            if(q + 1 < n && present[q + 1]){
                auto it = *m.lower_bound(q);
                sum += it.second;
                cur = it.first;
            }
            m[cur] = sum + nums[q];
            ans[i] = maxSum;
            maxSum = max(m[cur],maxSum);
        }
        return ans;
    }
};
