                m.erase(q-1);
            }
            if(q + 1 < n && present[q + 1]){
                auto it = *m.lower_bound(q);
                sum += it.second;
                cur = it.first;
            }
            m[cur] = sum + nums[q];
            ans[i] = maxSum;
            maxSum = max(m[cur],maxSum);
        }
        return ans;
    }
};
