#define ll long long
class Solution {
public:
    ll pre[100100];
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        pre[0]=nums[0];
        for(ll i=1;i<nums.size();i++)
        {
            pre[i]=pre[i-1]+nums[i];
        }
        set<tuple<ll,ll,ll>>s;
        map<ll,ll>m;
        ll jhc=0;
        for(ll i:nums)
        {
            jhc+=i;
        }
        ll n=nums.size();
        s.insert(make_tuple(n-1,0,jhc));
        cout<<jhc<<"=jhc\n";
        m[-1*jhc]+=1;
        vector<ll>ans;
        for(ll i:removeQueries)
        {
            auto it=s.lower_bound(make_tuple(i,-1,0));
            ll sum=(get<2>(*it));
            ll sind=get<1>(*it);
            ll eind=get<0>(*it);
            s.erase(*it);
            m[-1*sum]-=1;
            if(m[-1*sum]==0)
            {
                m.erase(-1*sum);
            }
            ll sum1=pre[i]-pre[sind]+nums[sind]-nums[i];
            ll sum2=pre[eind]-pre[i];
            m[-1*sum1]+=1;
            m[-1*sum2]+=1;
            if(sind<=i-1)
            {
                s.insert(make_tuple(i-1,sind,sum1));
            }
            if(i+1<=eind)
            {
                s.insert(make_tuple(eind,i+1,sum2));                
            }
            auto kk=m.begin();
            ans.push_back(-1*(kk->first));
        }
        return ans;
    }
};
