class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& num, vector<int>& que) {
        int n=num.size();
        vector<long long> ans;
        vector<long long> nums(n);
        nums[0]=num[0];
        for(int i=1;i<n;i++)  //store prefix sum in nums (prefix sum is used to calculate sum of a segment)
            nums[i]=nums[i-1]+num[i];

		//define and initialize map and set
        map<pair<int,int>,long long> m;  //pair is for starting and end indices of a segment and sum for that segment is stored as value
        m[{0,n-1}]=nums[n-1];
        multiset<long long> s;  //sum of all current segments are stored in set
        s.insert(nums[n-1]);

        for(int i=0;i<n-1;i++){
            long long id=que[i];
            auto it=m.lower_bound({id,0});  //find in which segment the index(to be removed) is present
            if(it==m.end() || id<(*it).first.first) it--;

            auto p=*it;
            int a=p.first.first,b=id-1,c=id+1,d=p.first.second;  //break that segment into two segments

            m.erase(it);   //delete current segment

            auto it2=s.find(p.second);
            s.erase(it2);      //delete the value of current segment from set

            if(b>=a){     // if end index > starting index of new segment, then add the segment in map and set
                long long s1=nums[b]-(a>0?nums[a-1]:0);
                m[{a,b}]=s1;
                s.insert(s1);
            }
            if(d>=c){     // same for the second new segment
                long long s2=nums[d]-(c>0?nums[c-1]:0);
                m[{c,d}]=s2;
                s.insert(s2);
            }
            ans.push_back(*s.rbegin());    //store the maximum value of current segments in the answer array
        }
        ans.push_back(0);   //last value will be 0 when all the indices are removed
        return ans;
    }
};
