class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& val) {
        reverse(val.begin(),val.end());
        vector<long long>ans;
        map<long long,pair<pair<long long,long long>,long long>>m;
        for(int i = 0; i < nums.size(); ++ i){
            m[i] = {{i,i},0};
        }
        long long pre = 0;
        ans.push_back(pre);
        for(auto a:val){
            long long cur = nums[a];
            long long left = -1, right = -1;
            if(a>0){
                left = m[a-1].first.first;
                cur += m[a-1].second;
            }
            if(a<nums.size()-1){
                right = m[a+1].first.second;
                cur += m[a+1].second;
            }
            if(left == -1){
                m[a] = {{0,right},cur};
                m[right-1] = {{0,right},cur};
            }else if(right == -1){
                m[a] = {{left,nums.size()-1},cur};
                m[left+1] = {{left,nums.size()-1},cur};
            }else{
                m[a] = {{left,right},cur};
                m[left+1] = {{left,right},cur};
                m[right-1] ={{left,right},cur};
            }
            pre = max(cur,pre);
            ans.push_back(pre);
        }
        ans.pop_back();
        reverse(ans.begin(),ans.end());
        return ans;
    }
};
