class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        const long long n=nums.size();
        vector<vector<long long>>groupinfo(n,{-1,-1,-1});
		//for i'th index
		// groupinfo[i][0] is startingIndex
		// groupinfo[i][1] is endingIndex
		// groupinfo[i][2] is sum
        vector<long long>res(n);
        long long mx=0;
        for(long long i=n-1;i>=0;--i){
            res[i]=mx;
            long long index=removeQueries[i];
            long long l,r,sum;
            if(index!=0 && index!=n-1 && groupinfo[index-1][0]!=-1 && groupinfo[index+1][0]!=-1){
                sum=groupinfo[index-1][2]+groupinfo[index+1][2]+nums[index];
                l=groupinfo[index-1][0];
                r=groupinfo[index+1][1];
                groupinfo[l][1]=r;
                groupinfo[l][2]=sum;
                groupinfo[r][0]=l;
                groupinfo[r][2]=sum;
            }else if(index!=0 && groupinfo[index-1][0]!=-1){
                l=groupinfo[index-1][0];
                sum=groupinfo[l][2]+nums[index];
                groupinfo[l][1]=index;
                groupinfo[l][2]=sum;
                groupinfo[index][0]=l;
                groupinfo[index][1]=index;
                groupinfo[index][2]=sum;
            }else if(index!=n-1 && groupinfo[index+1][0]!=-1){
                r=groupinfo[index+1][1];
                sum=groupinfo[r][2]+nums[index];
                groupinfo[r][0]=index;
                groupinfo[r][2]=sum;
                groupinfo[index][0]=index;
                groupinfo[index][1]=r;
                groupinfo[index][2]=sum;
            }else{
                groupinfo[index][0]=groupinfo[index][1]=index;
                groupinfo[index][2]=nums[index];
                sum=nums[index];
            }
            mx=max(mx,sum);
        }
        return res;
    }
};
