class Solution {
public:
    long long su(int l,int r,vector<long long> &v){ //prefix sum function for fast range sum calculation
        if(l==0) return v[r];
        else return v[r]-v[l-1];
    }
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& remo) {
        map<pair<long long,long long>,long long> mp;
        int n=nums.size();
        vector<long long> v(n,0);v[0]=nums[0];
        for(int i=1;i<n;i++) v[i]=v[i-1]+nums[i];
        multiset<long long> mt;
        mp[{0,n-1}]=su(0,n-1,v);
        mt.insert(su(0,n-1,v));
        vector<long long> ans;
        for(int i=0;i<n;i++){
            int x=remo[i];
            auto it = mp.upper_bound({x,1e9}); //finding in which segment the x is
            it--;
            int l=it->first.first,r=it->first.second;
            long long val=it->second;
            mt.erase(mt.find(val));
            mp.erase(it);
            if(l==r){}                  // do nothing if segment is single value
            else if(x==l){              //only add right segment after partion
                int l2=x+1,r2=r;
                if(l2<=r2){
                    mt.insert(su(l2,r2,v));
                    mp[{l2,r2}]=su(l2,r2,v);
                }
            }else if(x==r){              //only add left segment
                int l1=l,r1=x-1;
                if(l1<=r1){
                    mt.insert(su(l1,r1,v));
                    mp[{l1,r1}]=su(l1,r1,v);
                }
            }else{                         //otherwise
                int l1=l,r1=x-1,l2=x+1,r2=r;
                if(l1<=r1){
                    mt.insert(su(l1,r1,v));
                    mp[{l1,r1}]=su(l1,r1,v);
                }
                if(l2<=r2){
                    mt.insert(su(l2,r2,v));
                    mp[{l2,r2}]=su(l2,r2,v);
                }
            }
            auto it1=mt.end();
            if(!mt.empty()){it1--;ans.push_back(*it1);}
        }
        ans.push_back(0);     //adding 0 for the last element
        return ans;
    }
};

