```
class Solution {

    class SegTree{

        long maxSeg;
        long pmaxSeg;
        long smaxSeg;

        int pPointer;
        int sPointer;

        int left;
        int right;

        SegTree leftChild;
        SegTree rightChild;

        public SegTree(int[] a, int l, int r){

            pPointer = -1;
            sPointer = -1;
            left = l;
            right = r;

            if(l==r){
                maxSeg = a[l];
                pmaxSeg = a[l];
                smaxSeg = a[l];
                return;
            }

            int m = l+(r-l)/2;
            leftChild = new SegTree(a, l, m);
            rightChild = new SegTree(a, m+1, r);
            recalc();

        }

        public void update(int x){
            if(left==right){
                maxSeg = 0;
                pmaxSeg = 0;
                smaxSeg = 0;
                pPointer = left;
                sPointer = left;
                return;
            }
            if(x<=leftChild.right){
                leftChild.update(x);
            }else{
                rightChild.update(x);
            }
            recalc();
        }

        private void recalc(){

            pmaxSeg = leftChild.pmaxSeg;
            if(leftChild.pPointer == -1){
                pmaxSeg = rightChild.pmaxSeg + leftChild.pmaxSeg;
            }

            smaxSeg = rightChild.smaxSeg;
            if(rightChild.sPointer == -1){
                smaxSeg = leftChild.smaxSeg + rightChild.smaxSeg;
            }

            pPointer = leftChild.pPointer;
            if(leftChild.pPointer == -1) pPointer = rightChild.pPointer;

            sPointer = rightChild.sPointer;
            if(rightChild.sPointer == -1) sPointer = leftChild.sPointer;

            maxSeg = Math.max(leftChild.maxSeg, rightChild.maxSeg);

            //check if maxSeg lies half left and half right
            if(leftChild.pPointer == -1){
                maxSeg = Math.max(maxSeg, leftChild.maxSeg + rightChild.pmaxSeg);
            }

            if(rightChild.sPointer == -1){
                maxSeg = Math.max(maxSeg, rightChild.maxSeg + leftChild.smaxSeg);
            }

            maxSeg = Math.max(maxSeg, leftChild.smaxSeg + rightChild.pmaxSeg);


        }


    }

    boolean debug = false;


    public long[] maximumSegmentSum(int[] nums, int[] removeQueries) {

        int n = nums.length;
        SegTree seg = new SegTree(nums, 0, n-1);

        if(debug){

            System.out.println(seg.leftChild.maxSeg+" "+seg.rightChild.maxSeg);

        }

        int m = removeQueries.length;
        long[] ans = new long[m];
        int i = 0;

        for(int x: removeQueries){

            seg.update(x);
            if(debug){
                System.out.println(seg.leftChild.maxSeg+" "+seg.rightChild.maxSeg);
                System.out.println(seg.leftChild.smaxSeg+" "+seg.rightChild.pmaxSeg);
                System.out.println("----");
            }
            long y = seg.maxSeg;
            ans[i++] = y;
        }

        return ans;

    }




}

