```go
func max(x, y int64) int64 {
	if x > y {
		return x
	}
	return y
}

func maximumSegmentSum(nums []int, removeQueries []int) []int64 {
	var (
		n    = len(nums)
		res  = make([]int64, n)
		seen = make(map[int]int)
		psum = make([]int64, 1)
	)
	for i, num := range nums {
		psum = append(psum, psum[i]+int64(num))
	}
	for i := n - 1; i > 0; i-- {
		j := removeQueries[i]
		l, r := j, j
		if k, ok := seen[j-1]; ok {
			l = k
		}
		if k, ok := seen[j+1]; ok {
			r = k
		}
		seen[l], seen[r] = r, l
		res[i-1] = max(res[i], psum[r+1]-psum[l])
	}
	return res
}
