vector<long long> maximumSegmentSum(vector<int> &nums, vector<int> &removeQueries) {
    int n = nums.size();
    vector<long long> res(n, 0), psum(1, 0);
    unordered_map<int, int> seen;
    for (int i = 0; i < n; ++i) psum.push_back(*rbegin(psum) + nums[i]);
    for (int i = n - 1; i > 0; --i) {
        int j = removeQueries[i], l = seen.count(j - 1) ? seen[j - 1] : j, r = seen.count(j + 1) ? seen[j + 1] : j;
        seen[l] = r, seen[r] = l;
        res[i - 1] = max(res[i], psum[r + 1] - psum[l]);
    }
    return res;
}
