    class BST {
      private root: LinkedList | null;
      constructor() {
        this.root = null;
      }

      private rotateLeft = function (x: LinkedList) {
        let y = x.right!;
        y.p = x.p;

        if (x.p === null) this.root = y;
        else if (x.p.left === x) x.p.left = y;
        else x.p.right = y;

        x.right = y.left;
        if (x.right !== null) x.right.p = x;

        y.left = x;
        x.p = y;
      };
      private rotateRight = function (y: LinkedList) {
        let x = y.left!;
        x.p = y.p;

        if (y.p === null) this.root = x;
        else if (y.p.left === y) y.p.left = x;
        else y.p.right = x;

        y.left = x.right;
        if (y.left !== null) y.left.p = y;

        x.right = y;
        y.p = x;
      };
      private insertFixUp = function (x: LinkedList | null) {
        while (x && x.p !== null && x.p.color === "R" && x.p.p) {
          if (x.p === x.p.p.left) {
            let y = x.p.p.right;
            if (y !== null && y.color === "R") {
              x.p.color = "B";
              y.color = "B";
              x.p.p.color = "R";
              x = x.p.p;
            } else {
              if (x === x.p.right) {
                x = x.p;
                this.rotateLeft(x);
              }

              x.p!.color = "B";
              x.p!.p.color = "R";
              this.rotateRight(x.p!.p);
            }
          } else {
            let y = x.p.p.left;
            if (y !== null && y.color === "R") {
              x.p.color = "B";
              y.color = "B";
              x.p.p.color = "R";
              x = x.p.p;
            } else {
              if (x === x.p.left) {
                x = x.p;
                this.rotateRight(x);
              }

              x.p!.color = "B";
              x.p!.p.color = "R";
              this.rotateLeft(x.p!.p);
            }
          }
        }
        this.root.color = "B";
      };
      insertNode = function (key: number) : number {
        let t: LinkedList | null = this.root;
        let y = t;
        let node = new LinkedList(key, "R");
        let min = -1;
        while (t !== null) {
          y = t;
          if (key < t.key) t = t.left;
          else {
            min = t.key;
            t = t.right;
          }
        }

        if (y === null) this.root = y = node;
        else {
          t = node;
          if (key < y.key) {
            y.left = t;
          } else y.right = t;
          t.p = y;
        }

        this.insertFixUp(node);
        return min;
      };
    }

    class PriorityQueue {
        private pQueue : {sum: number, lowerRangeIndex: number}[];
        private lowerRngIndexPair: Map<number, number>;
        private heapSize : number;
        constructor() {
            this.pQueue = [];
            this.heapSize = 0;    
            this.lowerRngIndexPair = new Map<number, number>();
        }
        heapMax = function () : number {
            return (this.pQueue[0] as {sum: number, lowerRangeIndex: number}).sum;
        }
        pop = function (lwrRngIndex: number) : number {
            let hpIdx = (this.lowerRngIndexPair as Map<number, number>).get(lwrRngIndex)!;
            let max = (this.pQueue[hpIdx] as {sum: number, lowerRangeIndex: number}).sum;
            this.swapElements(hpIdx, this.heapSize - 1);
            (this.lowerRngIndexPair as Map<number, number>).delete(lwrRngIndex);
            this.heapSize--;

            let i = hpIdx;
            let parent = Math.ceil(i / 2) - 1;
            if (parent >= 0 && i > 0 && this.pQueue[i].sum >= this.pQueue[parent].sum) {
              while (parent >= 0) {
                if (this.pQueue[i].sum > this.pQueue[parent].sum) {
                  this.swapElements(i, parent);
                  i = parent;
                  parent = Math.ceil(i / 2) - 1;
                } else break;
              }
            }
            else this.maxHeap(hpIdx);
            return max;

        }
        insert = function (sum : number, lowerRangeIndex: number) {
            this.heapSize++;
            this.pQueue[this.heapSize - 1] = { sum: sum, lowerRangeIndex: lowerRangeIndex };
            (this.lowerRngIndexPair as Map<number, number>).set(lowerRangeIndex, this.heapSize - 1);
            if (this.heapSize === 1)
                return;

            let i = this.heapSize - 1;
            let parent = Math.ceil(i / 2) - 1;

            while (parent >= 0) {
                if (this.pQueue[i].sum > this.pQueue[parent].sum) {
                    this.swapElements(i, parent);
                    i = parent;
                    parent = Math.ceil(i / 2) - 1;
                }
                else
                    break;
            }
        }
        private swapElements(i : number, j : number) {
            let temp = this.pQueue[i];
            this.pQueue[i] = this.pQueue[j];
            this.lowerRngIndexPair.set(this.pQueue[i].lowerRangeIndex, i);
            this.pQueue[j] = temp;
            this.lowerRngIndexPair.set(this.pQueue[j].lowerRangeIndex, j);
        }
        private maxHeap(i: number) {
            let leftChildIndex = 2 * i + 1;
            if (leftChildIndex >= this.heapSize)
                return;

            let rightChildIndex = leftChildIndex + 1;
            if (rightChildIndex >= this.heapSize) {
                if (this.pQueue[i].sum < this.pQueue[leftChildIndex].sum)
                    this.swapElements(i, leftChildIndex);
            }
            else if (this.pQueue[i].sum < this.pQueue[leftChildIndex].sum || this.pQueue[i].sum < this.pQueue[rightChildIndex].sum) {
                if (this.pQueue[leftChildIndex].sum <= this.pQueue[rightChildIndex].sum) {
                    this.swapElements(i, rightChildIndex);
                    this.maxHeap(rightChildIndex);
                }
                else if (this.pQueue[rightChildIndex].sum < this.pQueue[leftChildIndex].sum) {
                    this.swapElements(i, leftChildIndex);
                    this.maxHeap(leftChildIndex);
                }
            }
        }
    }

    function maximumSegmentSum(nums: number[], removeQueries: number[]): number[] {

     if(nums.length === 1) return [0];
      const sumNums : number[] = [nums[0]];
      const maxSegSum : number[] = [];
      for(let i = 1; i< nums.length; i++) {
        sumNums[i] = sumNums[i - 1] + nums[i];
      }

      const calcRangeSum = (l1:number, l2:number) : number => {
        let res = sumNums[l2];
        res -= ((l1 === 0) ? 0 : sumNums[l1 - 1]);
        return res;
      }

      const tree = new BST();
      const pq = new PriorityQueue();
      {
        let upperRIndx = removeQueries[0];
        let lowerRIndx = tree.insertNode(upperRIndx);
        if(upperRIndx !== 0) {
          let rSum = calcRangeSum(lowerRIndx + 1, upperRIndx - 1);
          pq.insert(rSum, lowerRIndx);
        }
        if(upperRIndx != nums.length - 1) {
          let rSum = calcRangeSum(upperRIndx + 1, nums.length - 1);
          pq.insert(rSum, upperRIndx);
        }
        maxSegSum.push(pq.heapMax());
      }
      for (let i = 1; i < removeQueries.length - 1; i++) {
        let upperRIndx = removeQueries[i];
        let lowerRIndx = tree.insertNode(upperRIndx);
        let totalSum = pq.pop(lowerRIndx);
        let lSum = (lowerRIndx < upperRIndx - 1) ? calcRangeSum(lowerRIndx + 1, upperRIndx - 1) : 0;
        let rSum = totalSum - lSum - nums[removeQueries[i]];
        if (lSum) pq.insert(lSum, lowerRIndx);
        if (rSum) pq.insert(rSum, upperRIndx);
        maxSegSum.push(pq.heapMax());
      }
      maxSegSum.push(0);
      return maxSegSum;
    };

