Option 2, use multiset , forward simulation O(nlog(n))

```
class Solution {
public:
    int n;
    vector<long long> prefix;
    vector<long long> ans;
    multiset<long long> mp;
    set<int> seg;
    long long getsum(int l, int r) {
        if (l > r) return 0;
        return prefix[r + 1] - prefix[l];
    }
    // sum by seg
    long long getsum1(int l, int r) {
        return getsum(l + 1, r -1);
    }
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& rq) {
        n = nums.size();
        prefix = vector<long long>(n + 1, 0);
        for (int i = 0; i < n; i++) prefix[i + 1] = (long long)(nums[i]) + prefix[i];
        mp.insert(prefix[n]);
        seg.insert(n);
        seg.insert(-1);
        for (auto& q : rq) {
            auto r = seg.upper_bound(q);
            int b = *r;
            r--;
            int a = *r;
            //cout << q << "," << a << "," << b << endl;
            long long s = getsum1(a, b);
            auto iter = mp.find(s);
            mp.erase(iter);

            long long ls = getsum1(a, q);
            long long rs = getsum1(q, b);
            if (ls > 0) mp.insert(ls);
            if (rs > 0) mp.insert(rs);
            long long cur = mp.empty() ? 0 : *mp.rbegin();
            //cout << q << "," << ls << "," << rs << "," << cur << endl;
            ans.push_back(cur);
            seg.insert(q);
        }
        return ans;
    }
};
