/**
 * @param {number[]} nums
 * @param {number[]} removeQueries
 * @return {number[]}
 */
var maximumSegmentSum = function(nums, removeQueries) {
    const res = Array(nums.length).fill(0) 
    const ds = Array(nums.length).fill(Number.MAX_VALUE);
    for (let i = removeQueries.length - 1; i > 0; --i) {
        let j = removeQueries[i];
        ds[j] = -nums[j];
        if (j > 0 && ds[j - 1] !== Number.MAX_VALUE)
            merge(j, j - 1, ds);
        if (j < nums.length - 1 && ds[j + 1] !== Number.MAX_VALUE)
            merge(j, j + 1, ds);
        res[i - 1] = Math.max(res[i], -ds[find(j, ds)]);
    }
    return res;
};

function find(i, ds) {
    return ds[i] < 0 ? i : ds[i] = find(ds[i], ds);
}

function merge(s1, s2, ds) {
    let p1 = find(s1, ds);
    let p2 = find(s2, ds);
    ds[p2] += ds[p1];
    ds[p1] = p2;
}
