class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        int n=nums.size();
        multiset<long long> s;
        map<int,long long> m;
        vector<long long> dp(n);

        dp[0]=nums[0];
        for(int i=1;i<n;i++){
            dp[i]=dp[i-1]+nums[i];    
        }
        s.insert(dp[n-1]);

        m[-1]=0;
        m[n]=dp[n-1];
        vector<long long> ans(n);
        for(int i=0;i<n;i++){
            auto it=m.lower_bound(removeQueries[i]);
            auto it1=prev(it);


            s.erase(s.find(dp[it->first-1]-it1->second));
            if(it->first-1>removeQueries[i])
                s.insert(dp[it->first-1]-dp[removeQueries[i]]);
            if(removeQueries[i]-1>it1->first)
                s.insert(dp[removeQueries[i]-1]-it1->second);

            m[removeQueries[i]]=dp[removeQueries[i]];
            ans[i]=(s.empty() ? 0 : *s.rbegin());
        }

        return ans;
    }
};
