vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries)
{
    vector<long long> ans;
    vector<int64_t> pref(nums.size() + 1);
    for (int i = 0; i < nums.size(); ++i) {
        pref[i + 1] = pref[i] + nums[i];
    }
    map<int, int> ranges;
    multiset<int64_t> max_sum = { 0 };
    auto add = [&](int l, int r) {
        if (l < r) {
            ranges[r] = l;
            max_sum.insert(pref[r] - pref[l]);
        }
    };
    add(0, nums.size());
    for (int i = 0; i < removeQueries.size(); ++i) {
        int j = removeQueries[i];
        auto [r, l] = *ranges.lower_bound(j);
        max_sum.erase(max_sum.find(pref[r] - pref[l]));
        add(l, j);
        add(j + 1, r);
        ans.push_back(*max_sum.rbegin());
    }
    return ans;
}
