**Logic:**
For every input query, you want to find the segment to which it belongs. Due to removal of this query, your segment splits into two new segments. 
I have maintained two segment tree to get the segment the query belongs to, namely, st_seg_tree and en_seg_tree.

getRightMost -> returns you the right most element in the st_seg_tree which corresponds to the starting indices of all the segments created
getLeftMost -> returns you the left most element in the en_seg_tree which corresponds to the ending indices of all the segments created
*range passed in the argument is a function of the index which is being removed*

Once you have the segment to which your query belongs to, you have to update the set structure by removing the current segment sum and adding the new segment sums. For this purpose, you can use a prefix sum array to get the segment sum in O(1) time. 

Overall time complexity of the code is O(N logN)

```
class Solution {
public:
    void updateStartSegTree(int i, int idx, int l, int r, vector<int>& st_seg_tree)
    {
        //cout << "updateStartSegTree input: i = " << i << ", idx = " << idx << ", l = " << l << ", r = " << r << endl;
        if(l==r)
        {
            st_seg_tree[idx] = i;
            return;
        }

        int mid = (l+r) >> 1;
        if(i<=mid)
            updateStartSegTree(i, 2*idx+1, l, mid, st_seg_tree);
        else
            updateStartSegTree(i, 2*idx+2, mid+1, r, st_seg_tree);

        if(st_seg_tree[2*idx+2] != -1)
            st_seg_tree[idx] = st_seg_tree[2*idx+2];
        else
            st_seg_tree[idx] = st_seg_tree[2*idx+1];

        return;
    }
    void updateEndSegTree(int i, int idx, int l, int r, vector<int>& en_seg_tree)
    {
        //cout << "updateEndSegTree input: i = " << i << ", idx = " << idx << ", l = " << l << ", r = " << r << endl;
        if(l==r)
        {
            en_seg_tree[idx] = i;
            return;
        }

        int mid = (l+r)>>1;
        if(i<=mid)
            updateEndSegTree(i, 2*idx+1, l, mid, en_seg_tree);
        else
            updateEndSegTree(i, 2*idx+2, mid+1, r, en_seg_tree);

        if(en_seg_tree[2*idx+1] != -1)
            en_seg_tree[idx] = en_seg_tree[2*idx+1];
        else
            en_seg_tree[idx] = en_seg_tree[2*idx+2];

        return;
    }
    int getRightMost(int idx, int ql, int qr, int l, int r, vector<int>& st_seg_tree)
    {
        //cout << "getRightMost input: idx = " << idx << ", ql = " << ql << ", qr = " << qr << ", l = " << l << ", r = " << r << endl;
        if(ql==l && qr==r)
            return st_seg_tree[idx];

        int mid = (l+r)>>1;
        if(qr <= mid)
            return getRightMost(2*idx+1, ql, qr, l, mid, st_seg_tree);
        if(ql>mid)
            return getRightMost(2*idx+2, ql, qr, mid+1, r, st_seg_tree);

        int ans_l = getRightMost(2*idx+1, ql, mid, l, mid, st_seg_tree);
        int ans_r = getRightMost(2*idx+2, mid+1, qr, mid+1, r, st_seg_tree);
        if(ans_r != -1)
            return ans_r;
        return ans_l;
    }
    int getLeftMost(int idx, int ql, int qr, int l, int r, vector<int>& en_seg_tree)
    {
        //cout << "getLeftMost input: idx = " << idx << ", ql = " << ql << ", qr = " << qr << ", l = " << l << ", r = " << r << endl;
        if(ql==l && qr==r)
            return en_seg_tree[idx];

        int mid = (l+r)>>1;
        if(qr<=mid)
            return getLeftMost(2*idx+1, ql, qr, l, mid, en_seg_tree);
        if(ql>mid)
            return getLeftMost(2*idx+2, ql, qr, mid+1, r, en_seg_tree);

        int ans_l = getLeftMost(2*idx+1, ql, mid, l, mid, en_seg_tree);
        int ans_r = getLeftMost(2*idx+2, mid+1, qr, mid+1, r, en_seg_tree);

        if(ans_l != -1)
            return ans_l;
        return ans_r;
    }
    void printSet(set<tuple<long long, int, int>>& s)
    {
        auto it = s.begin();
        cout << "printing set" << endl;
        while(it != s.end())
        {
            cout << get<0>(*it) << ", " << get<1>(*it) << ", " << get<2>(*it) << endl;
            it++;
        }
    }
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) 
    {
        // Approach 1 - My own idea using segment tree
        int n = nums.size();
        vector<int> st_seg_tree(4*n, -1);
        vector<int> en_seg_tree(4*n, -1);
        updateStartSegTree(0, 0, 0, n-1, st_seg_tree);
        updateEndSegTree(n-1, 0, 0, n-1, en_seg_tree);

        vector<long long> pref_sum(n, 0);

        for(int i= 0; i< n; i++)
        {
            pref_sum[i] = nums[i];
            if(i-1>=0)
                pref_sum[i] += pref_sum[i-1];
        }

        set<tuple<long long,int,int>> s; // <-sum, st, en> for every segment is stored
        s.insert({-pref_sum[n-1], 0, n-1});
        vector<long long> ans;
        vector<int> rem(n, 0);

        for(int i= 0; i< n-1; i++)
        {
            int idx = removeQueries[i];
            int st_idx = getRightMost(0, 0, idx, 0, n-1, st_seg_tree);
            int en_idx = getLeftMost(0, idx, n-1, 0, n-1, en_seg_tree);

            rem[idx] = 1;
            if(idx-1>=0 && rem[idx-1] == 0)
                updateEndSegTree(idx-1, 0, 0, n-1, en_seg_tree);
            if(idx+1<n && rem[idx+1] == 0)
                updateStartSegTree(idx+1, 0, 0, n-1, st_seg_tree);

            long long curr_sum = pref_sum[en_idx];
            if(st_idx-1 >= 0) curr_sum -= pref_sum[st_idx-1];
            s.erase({-curr_sum, st_idx, en_idx});

            if(idx-1 >= st_idx)
            {
                long long val = pref_sum[idx-1];
                if(st_idx-1 >= 0) val -= pref_sum[st_idx-1];
                s.insert({-val, st_idx, idx-1});
            }

            if(idx+1 <= en_idx)
            {
                long long val = pref_sum[en_idx];
                if(idx >= 0) val -= pref_sum[idx];
                s.insert({-val, idx+1, en_idx});
            }

            auto it = s.begin();
            ans.push_back(-1*get<0>(*it));
        }
        ans.push_back(0);
        return ans;
    }
};
```

