class Solution {
    public long[] maximumSegmentSum(int[] nums, int[] removeQueries) {
        int length = nums.length;
        long[] res= new long[length];
        UnionFind uf=new UnionFind(length);

        TreeSet<Long> set = new TreeSet<>();

        for(int i=length-1;i>=0;i--){
            uf.sum[removeQueries[i]]=nums[removeQueries[i]];
            if(removeQueries[i]+1<length&&uf.sum[removeQueries[i]+1]!=0){
                uf.union(removeQueries[i], removeQueries[i]+1);
            }

             if(removeQueries[i]>0&&uf.sum[removeQueries[i]-1]!=0){
                uf.union(removeQueries[i], removeQueries[i]-1);
            }
            set.add(uf.sum[uf.find(removeQueries[i])]);

            if(i>0)
                res[i-1]=set.last();

        }

        return res;


    }
}

class UnionFind{
    int[] f;
    long[] sum;
    public UnionFind(int size){
        f = new int[size];
        sum = new long[size];
        for(int i = 0; i < size; i++){
            f[i] = i;
        }
    }
    public int find(int x){
        if (f[x] != x){
            f[x] = find(f[x]);
        }
        return f[x];
    }
    public void union(int x, int y){
        int fx = find(x);
        int fy = find(y);
        f[f[y]] = fx;
        sum[fx]+=sum[fy];
    }    
