            if(mp[sum] <= 0) 
                mp.erase(sum);

            if(l == r) {
                // do nothing
            }
            else if(l == q[i]) {
                sum = get_sum(l+1, r, psum);
                mp[sum] += 1;
                ranges.insert({{l+1, r}, sum});
            } 
            else if(r == q[i]) {
                sum = get_sum(l, r-1, psum);
                mp[sum] += 1;
                ranges.insert({{l, r-1}, sum});
            }
            else {
                sum = get_sum(l, q[i]-1, psum);
                mp[sum] += 1;
                ranges.insert({{l, q[i]-1}, sum});

                sum = get_sum(q[i]+1, r, psum);
                mp[sum] += 1;
                ranges.insert({{q[i]+1, r}, sum});
            }

            if(mp.size() == 0) 
                ans[i] = 0;
            else {
                auto tmp = --mp.end();
                ans[i] = tmp->first;
            }
        }

        return ans;
    }
};

