# This problem is similar to subarray sum for updates and queries using segment tree.


class Solution {
public:
    long long sum[400001];
    long long prefix_sum[400001];
    long long suffix_sum[400001];
    long long subarray_sum[400001];
    void build(int i,int l,int r,int index,long long num)
    {
        if(i<l or i>r)
            return;

        if(l == r && i == l)
        {
            sum[index] = num;
            prefix_sum[index] = num;
            suffix_sum[index] = num;
            subarray_sum[index] = num;
            return;
        }

        if(l>=r)
            return;

        int mid =(l+r)/2;

        build(i,l,mid,2*index+1,num);
        build(i,mid+1,r,2*index+2,num);

        long long buf = -1e18;
        sum[index] = max(buf,sum[2*index+1]+sum[2*index+2]);
        prefix_sum[index] = max(prefix_sum[2*index+1],sum[2*index+1]+prefix_sum[2*index+2]);
        suffix_sum[index] = max(suffix_sum[2*index+2],sum[2*index+2]+suffix_sum[2*index+1]);
        subarray_sum[index] = max(subarray_sum[2*index+1],subarray_sum[2*index+2]);
        subarray_sum[index] = max(subarray_sum[index],prefix_sum[2*index+2]+suffix_sum[2*index+1]);

    }



    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {

        long long n = nums.size();
        vector<long long> dp(n,0);

        for(int i = 0;i<n;i++)
        {
            build(i,0,n-1,0,nums[i]);
        }

        long long arr = -1e18;
        for(int i = 0;i<n-1;i++)
        {
            build(removeQueries[i],0,n-1,0,arr);
            dp[i] = subarray_sum[0];
        }
        dp[n-1] = 0;

        return dp;


    }
};

