**1. Use the map and the multiset**
```
/**
 * Time Complexity: O(n * log(n))
 * Space Complexity: O(n)
 * where `n` is the length of the vector `nums`
 */
class Solution {
 public:
  vector<long long> maximumSegmentSum(const vector<int> &nums, const vector<int> &removeQueries) {
    const int n = static_cast<int>(nums.size());
    long long presums[n + 1];
    presums[0] = 0LL;
    for (int i = 0; i < n; ++i) {
      presums[i + 1] = presums[i] + nums[i];
    }

    map<int, int> intervals;
    multiset<long long> interval_sums;
    intervals[0] = n;
    interval_sums.emplace(presums[n] - presums[0]);
    vector<long long> ret(n);
    for (int i = 0; i < n; ++i) {
      const int remove = removeQueries[i];
      auto itr = prev(intervals.upper_bound(remove));
      const auto [left, right] = *itr;
      intervals.erase(itr);
      const long long sum = presums[right] - presums[left];
      auto itr_sum = interval_sums.find(sum);
      interval_sums.erase(itr_sum);
      vector<pair<int, int>> segments;
      segments.emplace_back(left, remove);
      segments.emplace_back(remove + 1, right);
      for (const auto [begin, end] : segments) {
        if (begin < end) {
          intervals[begin] = end;
          interval_sums.emplace(presums[end] - presums[begin]);
        }
      }
      ret[i] = interval_sums.empty() ? 0 : *interval_sums.rbegin();
    }
    return ret;
  }
};
```
**2. Use the Disjoint Set**
```
/**
 * Time Complexity: O(n)
 * Space Complexity: O(n)
 * where `n` is the length of the vector `nums`
 */
class Solution {
 private:
  class DisjointSet {
   public:
    DisjointSet(const vector<int> &nums)
      : parents_(nums.size(), -1), ranks_(nums.size(), 0), sums_(nums.size()), max_sum_(0LL) {
        copy(nums.begin(), nums.end(), sums_.begin());
    }

    long long insert(const int index) {
      const int n = static_cast<int>(parents_.size());
      parents_[index] = index;
      max_sum_ = max(max_sum_, sums_[index]);
      if (index > 0 && parents_[index - 1] != -1) {
        max_sum_ = max(max_sum_, do_union(index - 1, index));
      }
      if (index + 1 < n && parents_[index + 1] != -1) {
        max_sum_ = max(max_sum_, do_union(index, index + 1));
      }
      return max_sum_;
    }

   private:
    int find(const int i) {
      if (i == parents_[i]) {
        return i;
      }

      parents_[i] = find(parents_[i]);
      return parents_[i];
    }

    long long do_union(const int i, const int j) {
      const int parent_i = find(i);
      const int parent_j = find(j);
      if (parent_i == parent_j) {
        return sums_[parent_i];
      }

      if (ranks_[parent_i] < ranks_[parent_j]) {
        parents_[parent_i] = parent_j;
        sums_[parent_j] += sums_[parent_i];
        sums_[parent_i] = 0;
      } else if (ranks_[parent_i] > ranks_[parent_j]) {
        parents_[parent_j] = parent_i;
        sums_[parent_i] += sums_[parent_j];
        sums_[parent_j] = 0;
      } else {
        // ranks_[parent_i] == ranks_[parent_j]
        parents_[parent_i] = parent_j;
        ++ranks_[parent_j];
        sums_[parent_j] += sums_[parent_i];
        sums_[parent_i] = 0;
      }
      return max(sums_[parent_i], sums_[parent_j]);
    }

    vector<int> parents_;
    vector<int> ranks_;
    vector<long long> sums_;
    long long max_sum_;
  };

 public:
  vector<long long> maximumSegmentSum(const vector<int> &nums, const vector<int> &removeQueries) {
    const int n = static_cast<int>(nums.size());
    DisjointSet ds(nums);
    vector<long long> ret(n);
    ret.back() = 0;
    for (int i = n - 1; i > 0; --i) {
      ret[i - 1] = ds.insert(removeQueries[i]);
    }
    return ret;
  }
};
```

