class Solution {
public:
	vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
		int n = nums.size();
		vector<long long> ans, prefixSum(n + 1);
		for(int i = 1; i <= n; i++) 
			prefixSum[i] = prefixSum[i - 1] + nums[i - 1];

		set<pair<int, int>> setIndex;
		setIndex.insert({0, n - 1});

		multiset<long long> setSum;
		setSum.insert(0);
		setSum.insert(prefixSum[n]);

		for(auto& index : removeQueries) {
			auto it = setIndex.upper_bound({index, n});
			it--;
			auto [start, end] = *it;

			long long currSum = prefixSum[end + 1] - prefixSum[start];

			setIndex.erase(it);
			setSum.erase(setSum.find(currSum));

			if(end > index) {
				setIndex.insert({index + 1, end});
				setSum.insert(prefixSum[end + 1] - prefixSum[index + 1]);
			} 

			if(start < index) {
				setIndex.insert({start, index - 1});
				setSum.insert(prefixSum[index] - prefixSum[start]);               
			}

			long long currMax = *setSum.rbegin();
			ans.push_back(currMax);
		}
		return ans;
	}
};

