    public int minNumberOfHours(int initialEnergy, int initialExperience, int[] energy, int[] experience) {
        int training = 0;  // keep track of how many hours needed to train
        int energyNum = initialEnergy;
        int experienceNum = initialExperience;
        for(int i = 0; i<energy.length; i++) { // loop through all the opponents
            if (energyNum <= energy[i]) {                // if you don't have enough energy
