            else {
			// training hours to increase experience to complete ith task
                training_hours+=experience[i]+1-curr_exp;
                curr_exp= experience[i]+1+ experience[i];

            }

        }
        int s=energy_sum-initialEnergy;

		//if energy sum is lesser than initial energy
        if(s<=0)
        {
            s=0;
        }
