                curr_experience += experience[i];
            }
            else
            {
                int req_experience = experience[i] + 1 - curr_experience;

                exercise_for_experience += req_experience;

                curr_experience +=  req_experience + experience[i];
            }
        }

        return exercise_for_energy + exercise_for_experience;
    }
};
