class Solution {
    public int minNumberOfHours(int initialEnergy, int initialExperience, int[] energy, int[] experience) {
        int ene = energy[energy.length - 1] + 1;
        int exp = experience[energy.length - 1] + 1;
        for (int i = energy.length - 2; i >= 0; i--) {
            ene = ene + energy[i];
            exp = Math.max(exp - experience[i], experience[i] + 1);
        }
        return Math.max(ene - initialEnergy, 0) + Math.max(exp - initialExperience, 0);
    }
}
