            // Case : 2
            if(initialExperience <= experience[i])
            {
                hours += experience[i] - initialExperience + 1;
                initialExperience += experience[i] - initialExperience + 1;
            }

            // Case : 3
            initialExperience += experience[i] ;
            initialEnergy -= energy[i];
        }
        return hours;
    }
