public:
    int minNumberOfHours(int initialEnergy, int initialExperience, vector<int>& energy, vector<int>& experience) {
        int TotalEnemyEnergy=0 , myExp=initialExperience , expTraining=0;

        for(int e:energy) TotalEnemyEnergy += e;  //total energy required to defeat enemy.

        //for experience training.
        for(int i=0;i<experience.size();i++){  //check enemies in order.
            if(myExp<=experience[i]){  //if training required to fight ith enemy.
