                ext+= (E[i] - idealE) + 1;
                idealE+=(E[i] - idealE) + 1;
            }
            idealE += E[i];
        }

        if(ideale <= 0) ext1 = (ideale*-1) + 1;

        return ext1 + ext;
    }
};
