public:
    int minNumberOfHours(int initialEnergy, int initialExperience, vector<int>& energy, vector<int>& experience) {
        int curr_experience = initialExperience;
        int curr_energy = initialEnergy;

        int additional_experience_needed = 0;
        int additional_energy_needed = 0;

        const auto N = energy.size();
        for(size_t i = 0; i < N; ++i) {
            // check if we need additional hours of trainings for experience
            int delta = max(0, experience[i] - curr_experience + 1);
            additional_experience_needed += delta;
            curr_experience += delta + experience[i];

            // check if we need additional hours of trainings for energy
            delta = max(0, energy[i] - curr_energy + 1);
            additional_energy_needed += delta;
            curr_energy += delta - energy[i];
        }

        return additional_energy_needed + additional_experience_needed;
    }
};
```

