class Solution {
    public int minNumberOfHours(int initialEnergy, int initialExperience, int[] energies, int[] experience) {
        return getNumberOfHoursForEnergy(initialEnergy, energies) + 
        getNumberOfHoursForExperience(initialExperience, experience);
    }

    private int getNumberOfHoursForEnergy(int initialEnergy, int[] energies) {
        int sum = getSum(energies);
        return initialEnergy > sum ? 0 : sum - initialEnergy + 1;
    }

    private int getNumberOfHoursForExperience(int initialExperience, int[] experience) {
        int hours = 0;

        for (int xp: experience) {
            if (initialExperience <= xp) {
                int diff = xp - initialExperience + 1; 
                hours += diff;
                initialExperience += diff;
            }

            initialExperience += xp;
        }

        return hours;
    }

    private int getSum(int[] arr) {
        int sum = 0;

        for (int num: arr)
            sum += num;

        return sum;
    }
}
