class Solution {
public:
    string largestPalindromic(string num) {

        // map
        map<char,int> mp;
        for(auto x : num){
            mp[x]++;
        }

        // vectors
        vector<char> notone , one;

        for(auto x : mp){
            if(x.second == 1){
                one.push_back(x.first);
            }else{
                if(x.second%2 ==1){
                    one.push_back(x.first);
                    mp[x.first]--;
                }
                notone.push_back(x.first);
            }
        }

        sort(one.begin(),one.end(),greater<int>());
        sort(notone.begin(),notone.end());

        deque<char> dq;
        // putting greatest element with 1 freq in string
        if(one.size()>0) dq.push_front(one[0]);

        // case of empty with 0
        if(notone.size()==1 and notone[0] == '0'){ if(dq.empty()) dq.push_front('0');}
        else{
            for(auto x : notone){
                int freq = mp[x]/2;
                while(freq-- > 0){
                    dq.push_back(x);
					dq.push_front(x);
                }
            }
        }

        string ans = "";
        while(!dq.empty()){
            ans += dq.front();
            dq.pop_front();
        }
        return ans;
    }
};
