class Solution {
    public String largestPalindromic(String num) {
        int[] map = new int[10];
		for (int n : num.toCharArray()) {
            map[n - '0']++;
        }

        int odd = -1;    //  stores the maximum number with odd frequency
        for (int i = 0; i < 10; i++) {
            if ((map[i] % 2) == 1) {
                odd = i;
            }
        }

		//  build (from right to left) the second half of the palindrome, using only half of the numbers
        StringBuilder answer = new StringBuilder();
        for (int i = 9; i >= 0; i--) {
            for (int c = 0; c < map[i] / 2; c++) {
                if (i == 0 && answer.length() == 0) break;    //  edge case
                answer.insert(0, i);
            }
        }

        StringBuilder a = new StringBuilder(answer);
        a.reverse();
        if (odd == -1) {
		    //  no odd number is seen and answer is empty() then return "0"
            if (answer.length() == 0) return "0";
			//  combine the two portions
            return a.toString() + answer.toString();
        }
		return a.toString() + odd + answer.toString();
    }
}

