class Solution {
public:
    string largestPalindromic(string num) {
        unordered_map<int, int> m, cm;
        string ans = "";

        for(int i=0; i<num.size(); i++){
            int n = num[i] - '0';
            m[n]++;
        }

        priority_queue<int> epq, opq;
        int oddBig = -1;

		// add freq and check the biggest odd number that can be placed in middle
        for(auto i:m){
            if(i.second%2 == 0) epq.push(i.first);
            else if(i.second > 1 && i.second%2 == 1) opq.push(i.first);
            if(i.second%2 == 1) oddBig = max(oddBig, i.first);
        }

		// copy of map
        cm = m;

		// generating first half of palindrome
        while(epq.size() && opq.size()){
            int e = epq.top(), o = opq.top();
            if(e>o){
                ans += (e + '0');
                cm[e]--;
                if(m[e]/2 >= cm[e]) epq.pop();
            } else {
                ans += (o + '0');
                cm[o]--;
                if(m[o]/2+1 >= cm[o]) opq.pop();
            }
        }

        while(epq.size()){
            int e = epq.top();
            ans += (e + '0');
            cm[e]--;
            if(m[e]/2 >= cm[e]) epq.pop();
        }

        while(opq.size()){
            int o = opq.top();
            ans += (o + '0');
            cm[o]--;
            if(m[o]/2+1 >= cm[o]) opq.pop();
        }

		// Adding other half of palindrome
        string temp;
        for(int i=ans.size()-1; i>=0; i--){
            temp+=ans[i];
        }

		// Check if some number can be added in middle
        if(oddBig == -1){
            ans+=temp;
        } else {
            ans += (oddBig+'0');
            ans+=temp;
        }

		// Remove precedig 0
        int i=0;
        while(ans[i] == '0'){
            ans = ans.substr(i+1, ans.size()-2);
        }

        // Handle for 0 if empty string -> "0000" after removing preceding 0's
        if(ans.size() == 0 && m[0]>=1){
            ans += '0';
        }

        return ans;
    }
};
```

