        // traverse through the hash table and push the even and odd pairs in ascending order
        for(int i=0;i<10;i++)
        {
            // we dont need to keep track of those numbers that didnt occur
            if(hash[i]==0) continue;

            // if the freq is odd
            if(hash[i]%2) 
            {
                // if the freq is not 1 that means it can be splitted as (freq-1) + 1
                // we did this as explained above if the freq is 5 it can be splitted into 4+1

                // we push the even freq into even vector
                if(hash[i]!=1) even.push_back({i,hash[i]-1});

                // and push the 1 into the odd
                odd.push_back({i,1});
            }
            // if the freq is even directly push it
            else even.push_back({i,hash[i]});
        }

        // initialize an empty string
        string ans="";

        // first we have to pick an element for the center of the palindromic string
        // palindromic string can be 11211 so if there are any num with odd freq 
        // we need to take the one with the highest value
        // for eg. in 444947137 the nums with odd freq are 1,3, and 9 
        // but we ll take the max element for the center of our palindrome
        if(odd.size()) ans =to_string(odd.back().first);

        // now we have to expand the string in both direction by adding the even freq digits
        // half of the freq of that digit will go to the left of the palindrome and half to the right

        // check if the only digit with even freq is '0' then if ans is empty return 0 else ans 
        if(even.size()==1 && even.back().first==0) return ans!=""?ans:"0";

        // we iterate over the even pairs and as the pairs were inserted in increasing order
        // always the smallest value will be inserted first 
        // For eg. we have 444947137 , currently our ans is '9'
        // even vector is ('4',4),('7',2)
        // first we insert half of '4' to the left and half to the right, new ans = '44'+'9'+'44'
        // then we insert half of '7' to the left and half to the right, new ans = '7'+'44944'+'7'
        for(auto [character,freq]: even)
        {
            ans = string(freq/2,character+'0') + ans + string(freq/2,character+'0');
        }

        return ans;
    }
};
