class Solution:
    def largestPalindromic(self, num: str) -> str:

        ans = []
        b = [str(x) for x in range(9, -1, -1)]
        from collections import defaultdict

        a = defaultdict(int)

        for x in num:
            a[x] += 1

        for x in b:
            n = len(ans)
            if n % 2 == 0:
                if a[x] > 0:
                    ans = ans[:n // 2] + [x] * a[x] + ans[n // 2:]
            else:
