def largestPalindromic(self, num: str) -> str:
	if num == '0'*len(num):
		return '0'
	cnter = Counter(num)
	head = ''
	mid = ''
	for k in '9876543210':
		v = cnter[k]
		if v%2:
			if not mid:
				mid = k
		head += k*(v//2)
	if head and head[0]=='0':
		head = ''
	return head+mid+head[::-1]
