        // strings to store the palindrome answers 
        string front = "";
        string back = "";

        for(int i = 9; i>=0; i--){
            // if front and back string is empty and we add zero to it, it will have leading zeros
                // which we don't want in our answer
            if(i == 0 && front.empty()){
                continue;
            }

            while(freq[i] > 1){
                front += to_string(i);
                back += to_string(i);
                freq[i]= freq[i] - 2;
            }
        }

        // We can also have a no whose freq is 1 in our answer and that no should be as large 
        // as possible as the question demanding the largest palindrome no therefore
        for(int i = 9; i>=0; i--){
            if(freq[i] >= 1){
                front += to_string(i);
                break;
            }
        }

        reverse(back.begin(), back.end());

        return front + back;
    }
};
