* ***Greedy***

* ***Time Complexity :- O(N)***

* ***Space Complexity :- O(Constant)***

```
class Solution {
public:
    string largestPalindromic(string num) {

        int n = num.size();

        // count array will store the frequency of each digit

        vector<int> count(10, 0);

        for(int i = 0; i < n; i++)
        {
            count[num[i] - '0']++;
        }

        string res = "";

        // iterate from last so that we can get largest possible digit first

        for(int i = 9; i >= 0; i--)
        {
            // escape leading zeros

            if(i == 0 && res.size() == 0)
                continue;

            // get the frequency of current diigt

            int x = count[i];

            // if frequency is > 0, then add the current digit to res x / times

            if(x > 0)
            {
                x /= 2;

                // decrement the count of curr digit

                count[i] -= 2 * x;

                // add the digit to res

                while(x > 0)
                {
                    res.push_back('0' + i);

                    x--;
                }
            } 
        }

        // we can put the one digit in middle of the res, still it will be palindrome, try to take the largest possible digit

        string temp = res;

        for(int i = 9; i >= 0; i--)
        {
            if(count[i] == 1)
            {
                res.push_back('0' + i);

                break;
            }
        }

        // reverse the temp

        reverse(temp.begin(), temp.end());

        // add to res

        res += temp;

        if(res.size() == 0)
            return "0";

        return res;
    }
};
