class Solution {
    public String largestPalindromic(String num) {
        int[] freq = new int[10];
        for(char ch : num.toCharArray()) {
            freq[ch - '0']++;
        }
        StringBuilder sb = new StringBuilder();
        int oddFreqMax = -1;
        for(int i = 0; i <= 9; i++) {
            if(freq[i] % 2 == 1) {
                oddFreqMax = i;
            }
        }
        if(oddFreqMax != -1) {
            sb.append(oddFreqMax);
        }
        for(int i = 0; i <= 9; i++) {
            int cnt = freq[i];
            if(cnt == 0) {
                continue;
            }
            if(cnt % 2 == 1) {
                cnt--;
            }
            for(int j = 0; j < cnt / 2; j++) {
                sb.append(i);
                sb.insert(0, i);
            }
        }
        int idx1 = 0;
        while(idx1 < sb.length() && sb.charAt(idx1) == '0') {
            idx1++;
        }
        sb.replace(0, idx1, "");
        int idx2 = sb.length() - 1;
        while(idx2 >= 0 && sb.charAt(idx2) == '0') {
            sb.deleteCharAt(idx2);
            idx2--;
        }
        return sb.length() == 0 ? "0" : sb.toString();
    }
}

