class Solution {
    public String largestPalindromic(String num) {
        boolean onlyContainsZeroes = true;
        int[] frequencies = new int[10];
        for (int i = 0; i < num.length(); i++) {
            int digit = num.charAt(i) - '0';
            if (digit != 0) {
                onlyContainsZeroes = false;
            }
            frequencies[digit]++;
        }
        if (onlyContainsZeroes) {
            return "0";
        }
        StringBuilder sb = new StringBuilder();
        boolean addedDigit = false;
        char digit = '.';
        for (int i = 9; i >= 0; i--) {   
            if (i == 0 && sb.isEmpty()) {
                continue;
            }
            if (frequencies[i] % 2 != 0 && addedDigit == false) {
                digit = (char) (i + '0');
                addedDigit = true;
            }
            for (int j = 0; j < frequencies[i] / 2; j++) {
                sb.append((char) (i + '0'));
            }         
        }     
        if (digit != '.') {
            return sb.toString() + digit + sb.reverse().toString();
        } else {
            return sb.toString() + sb.reverse().toString();
        }
    }
}
