    string largestPalindromic(string num) {
        if(num.size() == 1){
            return num;
        }
        vector<int> v(10 , 0);
        int n = num.size();
        for(int i=0; i<n; i++){
            v[num[i]-'0']++;
        }
        int singlemax = -1;
        for(int i=0; i<10; i++){
            if(v[i]%2 != 0){
                singlemax = max(i , singlemax);
            }
        }
        string ans = "";
        for(int i=9; i>=0; i--){
            if(v[i] > 1){
                if(i==0 && ans.size() == 0){
                    break;
                }
                ans.append(v[i]/2 , i+'0');
            }
        }
        num = ans;
        if(singlemax > -1){
            ans.append(1 , singlemax+'0');
        }
        reverse(num.begin() , num.end());
        ans+=num;
        if(ans.size() == 0) return "0";
        return ans;
    }
};
