class Solution {
public:
    string largestPalindromic(string nums) {
        int n=nums.size();
        unordered_map<char,int> mp;
        for(auto ele:nums) mp[ele]++;
        string res="";
        char odd='*';
        for(char i='9';i>='0';i--){
            if(mp.find(i)!=mp.end()){
                if(mp[i]%2!=0){
                    if(odd=='*') odd=i;
                }
                string temp= string(mp[i]/2,i);
                res+=temp;
            }
        }
        string rev=res;
        reverse(rev.begin(),rev.end());
        if(odd!='*') res=res+odd+rev;
        else res=res+rev;

        int i=0,j=res.size()-1;
        while(res[i]=='0'){
            i++,j--;
        }
        if(i>j) return "0";
        else return res.substr(i,j-i+1);
    }
};
