class Solution {
public:
    string largestPalindromic(string num) 
    {
        int n = num.size();
        map<int,int> mp;

        for(int i = 0;i<n;i++)
        {
            mp[num[i] - '0']++;
        }

        string firstHalf = "";
        string secondHalf = "";
        int middle = -1;

        for(int i = 9;i>=0;i--)
        {
            int freq = mp[i]/2;

            while(freq > 0)
            {
                firstHalf += to_string(i);
                freq--;
            }

            if(mp[i] % 2 != 0 && middle == -1)
            {
                middle = i;
            }
        }

        secondHalf = firstHalf;
        if(middle != -1)
        {
            firstHalf += to_string(middle);
        }

        reverse(secondHalf.begin(),secondHalf.end());
        firstHalf += secondHalf;   

        while(firstHalf.size() > 1 && firstHalf[firstHalf.size()-1] == '0')
        {
            firstHalf.erase(firstHalf.size()-1,1);
        }

        reverse(firstHalf.begin(),firstHalf.end());

        while(firstHalf.size() > 1 && firstHalf[firstHalf.size()-1] == '0')
        {
            firstHalf.erase(firstHalf.size()-1,1);
        }

        reverse(firstHalf.begin(),firstHalf.end());
        return firstHalf;
    }
};
