class Solution {
    func largestPalindromic(_ num: String) -> String {
        let arr = Array(num)
        var counter = [Character: Int]()
        for c in arr {
            counter[c, default: 0] += 1
        }
        var ans = ""
        var odd: Character?
        for key in counter.keys.sorted().reversed() {
