class Solution {
    public String largestPalindromic(String num) {
        StringBuilder first = new StringBuilder();

        char max = Character.MIN_VALUE;

        TreeMap<Character, Integer> map = new TreeMap<>(Collections.reverseOrder());

        for(char c : num.toCharArray())
            map.put(c, map.getOrDefault(c, 0)+1);

        for(Map.Entry<Character, Integer> m : map.entrySet()){
            char c = m.getKey();
            int f = m.getValue();

            if(f%2==0){
                int times = f;
                for(int i=0;i<times/2;i++){
                    if(c == '0' && first.length()==0)
                        break;
                    first.insert(0,c);
                }
            }
            else{
                int times = f-1;

                for(int i=0;i<times/2;i++){
                    if(c == '0' && first.length()==0)
                        break;
                    first.insert(0,c);
                }

                max = (char)Math.max((int)max, (int)c);
            }
        }

        StringBuilder second = new StringBuilder(first);
        second.reverse();

        String answer = max==Character.MIN_VALUE? 
            second.toString()+first.toString() : 
            second.toString()+max+first.toString();

        return answer.length()==0 ? "0" : answer;
    }
}
