        for (auto &c : num) ++ cnt[c - 48];
        for (int i = 9; i >= 0; -- i) {
            if (!ans.size() && !i) break;
            ans += string(cnt[i] >> 1, i + 48);
            if (cnt[i] % 2 && mid == 0x3f3f3f3f) mid = i;
