class Solution {
    public String largestPalindromic(String num) {
        TreeMap<String, Integer> maps = new TreeMap<>((a,b) -> b.compareTo(a));
        for(String s : num.split("")) maps.put(s, maps.getOrDefault(s, 0) + 1);
        StringBuilder sb = new StringBuilder(); 
        TreeSet<String> set = new TreeSet<>((a,b) -> b.compareTo(a));

        for(String s : maps.keySet()) {
                int size = maps.get(s)/2;
                while(size-- > 0) sb.append(s);
                if(maps.get(s) % 2 != 0) set.add(s);
        }

        return removeZeros(new StringBuilder(sb) + (
            !set.isEmpty() ?  set.first() : "") + sb.reverse());

    }

    private String removeZeros(String str) {
        int count = 0;
        for(int i = 0; i < str.length(); i++, count++) {
            if(str.charAt(i) != '0') break;
        }
        return (count >= str.length()) ? "0" : str.substring(count, str.length() - count);
    }
}
