        arr = []
        maxSingle = ''
        for k in cnt:
            if cnt[k] % 2 == 1:
                if maxSingle == '':
                    maxSingle = k
                elif maxSingle < k:
                    maxSingle = k

                if cnt[k] != 1:
                    arr.append([k, cnt[k] - 1])
            else:
                arr.append([k, cnt[k]])

        arr.sort(reverse = True)
        res1 = ""
        for k, c in arr:
            if k != '0':
                res1 += k * (c//2)
        if res1 != "" and cnt['0'] != 0:
            res1 += '0' * (cnt['0']//2)

        return res1 + maxSingle + res1[::-1]
